export default function Board(data) {
    return (
        <div className="board">
            {data.map((grp, grpI) => (
                <div key={grp.title} className="group">
                    <div className="title">{grp.title}</div>
                    {grp.items.map((item, itemI) => (
                        <div key={item.id} draggable className="item">
                            <div>
                                <p>{item.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    )
}
